{
    "name": "discover-sys-admin",
    "description": "Discover System Administration",
    "webservers": {
        "enable": false
    },
    "ssh-server-global-configs": {
        "enable": true,
        "enabled-vrfs": [
            "default"
        ]
    },
    "console": {
        "enable": true
    },
    "telnet-server": {
        "enable": false
    },
    "login-session-timeout": 15,
    "banner-message": {
        "message-delimiter": "~",
        "text": "IT Team only.\nNo access unless authorised!\n"
    }
}

{
  "auth-server": [
    {
        "name": "discover-cppm-1",
        "description": "Discover CPPM Server 1",
        "type": "RADIUS",
        "enable": true,
        "auth-server-address": "10.13.100.211",
        "auth-port": 1812,
        "acct-port": 1813,
        "dynamic-authorization-enable": true,
        "shared-secret-config": {
            "secret-type": "PLAIN_TEXT",
            "plaintext-value": "Aruba123"
        },
        "called-station-id": {
            "type": "MAC_ADDRESS",
            "mac-address-delimiter": "NONE",
            "upper-case": false,
            "include-ssid": false
        }
    },
    {
        "name": "discover-cppm-2",
        "description": "Discover CPPM Server 2",
        "type": "RADIUS",
        "enable": true,
        "auth-server-address": "10.13.100.212",
        "auth-port": 1812,
        "acct-port": 1813,
        "dynamic-authorization-enable": true,
        "shared-secret-config": {
            "secret-type": "PLAIN_TEXT",
            "plaintext-value": "Aruba123!"
        },
        "called-station-id": {
            "type": "MAC_ADDRESS",
            "mac-address-delimiter": "NONE",
            "upper-case": false,
            "include-ssid": false
        }
    }
  ]
}

{
    "interface": [
        {
            "id": 100,
            "description": "MGW-MGMT",
            "is-valid": true,
            "enable": true,
            "bcmc-optimization": false,
            "oper-state-up": false,
            "ipv4": {
                "nat-inside": false,
                "nat-outside": false,
                "forwarding": true,
                "suppress-arp": false
            },
            "igmp": {
                "enable": false
            }
        },
        {
            "id": 42,
            "description": "WLAN-USERS",
            "is-valid": true,
            "enable": true,
            "bcmc-optimization": true,
            "oper-state-up": false,
            "ipv4": {
                "nat-inside": false,
                "nat-outside": false,
                "forwarding": true,
                "suppress-arp": true
            },
            "igmp": {
                "enable": false
            }
        }
    ]
}

{
  "profile": [
    {
        "name": "discover-gw-sys",
        "description": "Discover GW System",
        "ipv4-configuration": {
            "device-interface-type": "VLAN",
            "device-vlan": 100
        },
            "timezone": {
            "name": "Australia/Melbourne"
        },
        "firewall": {
            "jumbo": true,
            "jumbo-mtu": 9216,
            "deny-inter-user-bridging": false,
            "deny-inter-user-traffic": false,
            "voip-qos-trusted": false
        }
    }
  ]
}

{
    "profile": [
        {
            "profile-name": "discover-gw-port",
            "description": "Uplink port for Discover MGW",
            "speed": "SPEED_AUTO",
            "enable": true,
            "port-type": "LAN",
            "trusted": true,
            "duplex": "AUTO_DUPLEX",
            "jumbo-mtu": true,
            "trusted-vlans": [
                "1",
                "42",
                "100"
            ],
            "switchport": {
                "interface-mode": "TRUNK",
                "native-vlan": 1,
                "trunk-vlan-ranges": [
                    "1",
                    "42",
                    "100"
                ]
            },
            "poe": {
                "enabled": false
            },
            "lldp": {
                "mode": "TX_RX",
                "transmit-interval": 30,
                "fast-transmit-interval": 1,
                "fast-transmit-counter": 4,
                "hold-interval": 4,
                "tlv": {
                    "med-extension": true
                }
            },
            "stp": {
                "enable": false,
                "bpdu-guard": false,
                "admin-edge-port": false,
                "admin-edge-port-trunk": false
            }
        }
    ]
}

{
  "wlan-ssid": [
    {
        "ssid": "discover-psk",
        "enable": true,
        "forward-mode": "FORWARD_MODE_BRIDGE",
        "dmo": {
            "enable": true,
            "channel-utilization-threshold": 90,
            "clients-threshold": 6
        },
        "broadcast-filter-ipv4": "BCAST_FILTER_ARP",
        "broadcast-filter-ipv6": "UCAST_FILTER_RA",
        "optimize-mcast-rate": false,
        "essid": {
            "use-alias": false,
            "name": "Discover-PSK"
        },
        "advertise-apname": true,
        "dot11k": true,
        "ftm-responder": true,
        "rf-band": "24GHZ_5GHZ",
        "g-legacy-rates": {
            "basic-rates": [
                "RATE_24MB",
                "RATE_36MB"
            ],
            "tx-rates": [
                "RATE_24MB",
                "RATE_36MB",
                "RATE_48MB",
                "RATE_54MB"
            ]
        },
        "a-legacy-rates": {
            "basic-rates": [
                "RATE_24MB",
                "RATE_36MB"
            ],
            "tx-rates": [
                "RATE_24MB",
                "RATE_36MB",
                "RATE_48MB",
                "RATE_54MB"
            ]
        },
        "high-throughput": {
            "enable": true,
            "very-high-throughput": true
        },
        "high-efficiency": {
            "enable": true
        },
        "extremely-high-throughput": {
            "enable": true,
            "mlo": true,
            "beacon-protection": false
        },
        "opmode": "WPA2_PERSONAL",
        "mac-authentication": false,
        "personal-security": {
            "passphrase-format": "STRING",
            "wpa-passphrase": "1234567890"
        },
        "vlan-selector": "NAMED_VLAN",
        "vlan-name": "bridged-wlan-vlan",
        "client-isolation": false
    }
  ]
}

